import React, { Component } from 'react';

import {connect} from 'react-redux'
import {fetchInsulins, selectInsulin} from '../Redux/actions'
import InsulinLog from './InsulinLog'


class LogContainer extends Component{

  componentDidMount(){
    this.props.fetchInsulins()

  }

  generateTabs=(insulins)=>{
    const {selectedInsulin , selectInsulin} = this.props
    return insulins.map(insulinObj=>{
      if (selectedInsulin === insulinObj.id){
        return <div className="active item"
        onClick={()=> selectInsulin(insulinObj.id)}>
        {insulinObj.insulin_name}
        </div>
      }else{
        return <div className="item" onClick={()=> selectInsulin(insulinObj.id)}>
        {insulinObj.insulin_name}>{insulinObj.insulin_name}</div>
      }

    })
  }


  render(){

    return(
      <div id='log'>

      <div className="ui top attached tabular menu">
        {this.generateTabs(this.props.insulins)}

      </div>
      <InsulinLog/>


      </div>
    )
  }
}

const mapStateToProps= (state)=>{
  return {insulins: state.insulins}
}

const mapDispatchToProps=(dispatch)=>{
  return{
    fetchInsulins:()=>dispatch(fetchInsulins()),
    selectInsulin:()=>dispatch(selectInsulin())
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(LogContainer)
