const initialState = {
  user:1,
  insulins:[],
  selectedInsulin: 4
}

const reducer = (state = initialState, action) =>{
  switch (action.type){
    case("GET_INSULINS"):{
      return {...state, insulins:action.payload}
    }
    case("SELECT_INSULIN"):{
      return {...state, selectedInsulin: action.payload}
    }
    case("ADD_ENTRY"):{
      const entryInsulin = action.payload.insulin_id
      const insulin= state.insulins.find(insulin)
      const newEntries = [...insulin.all_associated_entries, action.payload]
    }

    default:
      return state
  }
}

export {reducer}
