import React, { Component } from 'react';
import {connect} from 'react-redux'
import {postEntry} from '../Redux/actions'
import moment from 'moment'

class NewEntryForm extends Component{

  state = {

    // time:new Date(),
    time:'',
    // month:moment().month(),
    month:'',
    day:'',


    insulin:"",
    note:'',
    bloodSugar:'',
    numUnits:''
  }

  handleChange=(e)=>{
    this.setState({
      [e.target.name]: e.target.value
    }, ()=>console.log(this.state))
  }

  formatDateTime=()=>{
    console.log("TIME",this.state.time)
    console.log("MONTH",this.state.month)
    console.log("DAY",this.state.day)

    const time_to_combine = this.state.time

    const datetime_combined = new Date(`${this.state.day} ${this.state.month} ${time_to_combine} UTC`)
    return datetime_combined.toISOString()
  }

  handleSubmit=(e)=>{
    e.preventDefault()
    const insulinObj=this.props.insulins.find((insulin)=>{
      return insulin.insulin_name == this.state.insulin
    })

    // console.log("SHOULD BE USER ID", this.props.user.id)
    const newEntry = {
      user_id: this.props.user.id,
      entry_date_and_time: this.formatDateTime(),
      // entry_date_and_time: this.state.time.toISOString(),
      insulin_id : insulinObj.id,
      status: 'active',
      note: this.state.note,
      current_blood_sugar: this.state.bloodSugar,
      num_units_insulin: this.state.numUnits}
    this.props.postEntry(newEntry)
    this.setState({
      insulin: '',
      note:'',
      bloodSugar:'',
      numUnits:''
    })
  }

  generateInsulinDropDown=()=>{
    return this.props.insulins.map(insulin=><option>{insulin.insulin_name}</option>)
  }

  generateMonthDropDown=(monthArray, nowMonth)=>{
    return monthArray.map(month=>{
      if (month === nowMonth){
        return <option selected> {month}</option>
      }
      else{
        return <option>{month}</option>
      }
    })
  }


  render(){
    const monthArray= ["January", "February", "March", "April", "May",
    "June", "July", "August", "September", "October", "November", "December"]
    // const now = new Date()
    const nowMonth = monthArray[new Date().getMonth()]
    const nowDay = new Date().getDate()
    const nowTime = moment().format('LT')
    // console.log(monthArray[new Date().getMonth()])
    console.log(nowMonth)
    console.log(nowDay)
    console.log(nowTime)

    return(

      <form className='ui small form segment' id='newform' onSubmit={(e)=>this.handleSubmit(e)}>

      <div className='fields'>


      <div className='field'>

      <select name='insulin' value={this.state.insulin} onChange={(e)=>this.handleChange(e)}>
      <option value="" selected disabled hidden>select insulin</option>
      {this.generateInsulinDropDown()}
      </select>

      </div>





      <div className="two wide field">
        <label>Time</label>
        <input name='time' value={this.state.time} placeholder={nowTime} onChange={(e)=>this.handleChange(e)}></input>
        </div>


        <div className="field">
        <select name='month' value={this.state.month} onChange={(e)=>this.handleChange(e)}>
        {this.generateMonthDropDown(monthArray, nowMonth)}
        </select>
        </div>

          <div className="two wide field">
            <label>Day</label>
            <input name='day' value={this.state.day} placeholder={nowDay} onChange={(e)=>this.handleChange(e)}></input>
            </div>







      <div className="two wide field">
        <label>Blood Sugar</label>
        <input name='bloodSugar' value={this.state.bloodSugar} onChange={(e)=>this.handleChange(e)}></input>
        </div>



      <div className="two wide field">
        <label># Units</label>
        <input name='numUnits' value={this.state.numUnits} onChange={(e)=>this.handleChange(e)}></input>
        </div>




      <div className="field">
        <label>Note</label>
        <textarea rows="2" name='note' value={this.state.note} onChange={(e)=>this.handleChange(e)}></textarea>
        </div>

        <div className="field">
        <button id='add-new-entry-btn'>add to log</button>
        </div>
      </div>



      </form>

    )
  }
}



const mapStateToProps=(state)=>{
  return{
    insulins: state.insulins,
    user:state.auth.currentUser
}
}

const mapDispatchToProps=(dispatch)=>{
  return{
    postEntry:(newEntry)=>dispatch(postEntry(newEntry)),
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(NewEntryForm)
