import React, { Component } from 'react';
// import InsulinForm from './InsulinForm'
// import MyInsulins from './MyInsulins'

import {connect} from 'react-redux'
import {setUser, fetchInsulins, postNewUser} from '../Redux/actions'
import { withRouter } from "react-router-dom";

class SignUp extends Component{

  state={
    username:''

  }

  handleChange=(e)=>{
    this.setState({
      [e.target.name]: e.target.value
    }, ()=>console.log(this.state))
  }


  handleSignUp = () => {
    e.preventDefault()
    this.props.postNewUser(this.state)
    console.log(this.props.users)
    const user = this.props.users.find((user)=>user.username === this.state.username)
    this.props.setUser(user)
    console.log("U ID", user.id)
    this.props.fetchInsulins(user.id)

    this.props.history.push("/account")
    // .then(resp => localStorage.setItem("token", resp.token));
  // resp => localStorage.setItem("token", resp.token)
};


  handleSubmit=(e)=>{
    e.preventDefault();
     this.handleSignUp();
     this.props.history.push("/home");
  }



  render(){
    return(

        <div className="ui center aligned red  segment">
     <h1>Sign Up</h1>
      <form className="ui form" onSubmit={(e)=>this.handleSubmit(e)}>

      <div className= "inline field">
      <label>username:</label>
      <input name='username' value={this.state.username} onChange={(e)=>this.handleChange(e)}></input>
      </div>


      <button>Sign Up</button>

      </form>
      </div>

    )
  }
}

const mapStateToProps= (state)=>{
  return {
    users: state.users
  }
}


const mapDispatchToProps=(dispatch)=>{
  return{
    setUser:(user)=>dispatch(setUser(user)),
    fetchInsulins:(user)=>dispatch(fetchInsulins(user)),
    postNewUser:(newUser)=>dispatch(postNewUser(newUser))
  }
}


export default withRouter(connect(mapStateToProps, mapDispatchToProps)(SignUp))


