import React from 'react';
import moment from 'moment'
import {momentDurationFormatSetup} from 'moment-duration-format'
// import Countdown from 'react-countdown-now';
import Timer from './Timer'

const TimerCard=(props)=>{

  // calculateTimeLeft=()=>{
