export const fetchUser = (name)=>{
  return (dispatch)=>{
    const options={
      method: "POST",
      body: JSON.stringify({user:{username: name}}),
      headers:{
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    }
    return fetch(`http://localhost:3001/api/v1/login`, options)
    .then(res=>res.json())
    // .then(res=>console.log(res.user))
    .then((res)=>dispatch(setUser(res.user)))
    .catch(console.error)
  }
}


export const postNewUser = (newUser)=>{
  return (dispatch)=>{
    const options = {
      method: "POST",
      body: JSON.stringify(newUser),
      headers:{
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    }
    return fetch(`http://localhost:3001/api/v1/users`, options)
    .then(res=>res.json())
    .then((res) => dispatch(addUser(res)))
    // .catch(console.error)
    .then(action=>dispatch(setUser(action.payload.user)))
    .then(action=>dispatch(fetchInsulins(action.payload.id)))
  }
}
