import React, { Component } from 'react';

import {connect} from 'react-redux'
import {fetchInsulins} from '../Redux/actions'
import InsulinLog from './InsulinLog'


class LogContainer extends Component{

  componentDidMount(){
    this.props.fetchInsulins()
  }

  generateTabs=(insulins)=>{
    return insulins.map(insulinObj=>{
      if (this.state.selectedInsulin === insulinObj.id){
        return <div className="active item">{insulinObj.insulin_name}</div>
      }else{
        return <div className="item">{insulinObj.insulin_name}</div>
      }

    })
  }


  render(){

    return(
      <div id='log'>

      <div className="ui top attached tabular menu">
        {this.generateTabs(this.props.insulins)}

      </div>
      <InsulinLog insulin_id={this.state.active_tab}/>


      </div>
    )
  }
}

const mapStateToProps= (state)=>{
  return {insulins: state.insulins}
}

const mapDispatchToProps=(dispatch)=>{
  return{
    fetchInsulins:()=>dispatch(fetchInsulins()),
    selectInsulin:()=>dispatch(selectInsulin())
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(LogContainer)
