const initialState = {
  user:1,
  insulins:[],
  selectedInsulin: 4
}

const reducer = (state = initialState, action) =>{
  switch (action.type){
    case("GET_INSULINS"):{
      return {...state, insulins:action.payload}
    }
    case("SELECT_INSULIN"):{
      return {...state, selectedInsulin: action.payload}
    }
    case("ADD_ENTRY"):{
      const newInsulins = [...state.insulins].map((insulin)=>{
        if (insulin.id === action.payload.insulin_id){
          const insulinIndex = state.insulins.indexOf(insulin)
          const newEntries = [...insulin.all_associated_entries, action.payload]

          // const newData = {...insulin, all_associated_entries: [newEntries]}
          return [...state.insulins, (insulinIndex: {...insulin, all_associated_entries: newEntries})]
        }
    })
      return {...state, insulins:newInsulins}

    }

    default:
      return state
  }
}

export {reducer}
