import React, { Component } from 'react';
import HomeContainer from './Components/HomeContainer'
import NavBar from './Components/NavBar'
import Header from './Components/Header'
import LoginForm from './Components/LoginForm'
import AccountForm from './Components/AccountForm'
import { BrowserRouter, Route, Switch} from "react-router-dom";

import 'semantic-ui-css/semantic.min.css';

import {connect} from 'react-redux'
import {fetchInsulins} from './Redux/actions'



import './App.css';

class App extends Component {
  state = {
    auth: { currentUser: {} },
    newUser: {
      username: "",
      password: ""
    }
  };



  componentDidMount=()=>{
    const token = localStorage.getItem("token");
    if (token) {
      fetch("http://localhost:3000/api/v1/current_user", {
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: token
        }
      })
        .then(resp => resp.json())
        .then(user => {
          const currentUser = { currentUser: user };
          this.setState({
            auth: currentUser
          });
        });
    }
    // this.props.fetchInsulins(this.props.user)
  }

  handleLogout = () => {
    localStorage.removeItem("token");
    this.setState({ auth: { currentUser: {} } });
    this.props.history.push("/signup");
  }

  handleSignUp = user_info => {
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        user: {
          username: user_info.username,
          password: user_info.password
        }
      })
    })
      .then(resp => resp.json())
      .then(resp => localStorage.setItem("token", resp.token));
  };










  render() {
    return (
      <BrowserRouter>

      <div>

        <div className="ui three column grid">
          <div className="column">
            <NavBar handleLogout={this.handleLogout} />
          </div>
        <div className="column">
          <Header/>
        </div>
      </div>


      <Switch>
      <Route
          path="/signup"
          render={props => <AccountForm handleSignUp={this.handleSignUp} />}
        />


            <Route path="/login" component={LoginForm} />
            <Route path="/account" component={AccountForm} />
            <Route path="/" component={HomeContainer} />
          </Switch>

      </div>
      </BrowserRouter>

    );
  }
}

const mapStateToProps=(state)=>{
  return{user:state.user}
}

const mapDispatchToProps=(dispatch)=>{
  return{
    fetchInsulins:(user)=>dispatch(fetchInsulins(user))
    // fetchUsers:()=>dispatch(fetchUsers())
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(App);
